mapper.select:
SELECT NO, ID, PW, NAME, PHONE, EMAIL, ADDRESS, REGDATE, STATE
  FROM MEMBER
 WHERE ID = #{id}
   AND PW = #{pw}
   AND STATE NOT IN(-1)

SELECT COUNT(ID)
  FROM MEMBER
 WHERE ID = #{id}

SELECT COUNT(EMAIL)
  FROM MEMBER
 WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET PW = #{pw}
 WHERE NO = #{no}

UPDATE MEMBER
   SET NAME = #{name}
     , PHONE = #{phone}
     , ADDRESS = #{address}
 WHERE NO = #{no}

UPDATE MEMBER
   SET STATE = -1
 WHERE NO = #{no}
