mapper.select:
SELECT NO, ID, PW, NAME, PHONE, EMAIL, ADDRESS, REGDATE, STATE
  FROM MEMBER
 WHERE ID = #{id}
   AND PW = #{pw}

SELECT COUNT(ID)
  FROM MEMBER
 WHERE ID = #{id}

mapper.update:
UPDATE MEMBER
   SET PW = #{pw}
 WHERE NO = #{no}

UPDATE MEMBER
   SET NAME = #{name}
     , PHONE = #{phone}
     , ADDRESS = #{address}
 WHERE NO = #{no}
